// SPDX-License-Identifier: GPL-3.0
// Forked from https://github.com/ourzora/core @ 450cd154bfbb70f62e94050cc3f1560d58e0506a

pragma solidity >=0.8.4;
pragma experimental ABIEncoderV2;

import { IERC721 } from '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import { IMarket } from './IMarket.sol';
import { ILux } from './ILux.sol';
import { IDrop } from './IDrop.sol';

/**
 * @title Interface for Zoo Protocol's Media
 */
interface IMedia is IERC721 {
  struct EIP712Signature {
    uint256 deadline;
    uint8 v;
    bytes32 r;
    bytes32 s;
  }

  struct MediaData {
    // A valid URI of the content represented by this token
    string tokenURI;
    // A valid URI of the metadata associated with this token
    string metadataURI;
    // A SHA256 hash of the content pointed to by tokenURI
    bytes32 contentHash;
    // A SHA256 hash of the content pointed to by metadataURI
    bytes32 metadataHash;
  }

  event TokenURIUpdated(uint256 indexed _tokenId, address owner, string _uri);
  event TokenMetadataURIUpdated(uint256 indexed _tokenId, address owner, string _uri);

  /**
   * @notice Return the metadata URI for a piece of media given the token URI
   */
  function tokenMetadataURI(uint256 tokenId) external view returns (string memory);

  /**
   * @notice Mint new media for msg.sender.
   */
  function mint(MediaData calldata data, IMarket.BidShares calldata bidShares) external;

  /**
   * @notice EIP-712 mintWithSig method. Mints new media for a creator given a valid signature.
   */
  function mintWithSig(
    address creator,
    MediaData calldata data,
    IMarket.BidShares calldata bidShares,
    EIP712Signature calldata sig
  ) external;

  /**
   * @notice Transfer the token with the given ID to a given address.
   * Save the previous owner before the transfer, in case there is a sell-on fee.
   * @dev This can only be called by the auction contract specified at deployment
   */
  function auctionTransfer(uint256 tokenId, address recipient) external;

  /**
   * @notice Set the ask on a piece of media
   */
  function setAsk(uint256 tokenId, IMarket.Ask calldata ask) external;

  /**
   * @notice Remove the ask on a piece of media
   */
  function removeAsk(uint256 tokenId) external;

  /**
   * @notice Set the bid on a piece of media
   */
  function setBid(uint256 tokenId, IMarket.Bid calldata bid) external;

  /**
   * @notice Remove the bid on a piece of media
   */
  function removeBid(uint256 tokenId) external;

  function acceptBid(uint256 tokenId, IMarket.Bid calldata bid) external;

  /**
   * @notice Revoke approval for a piece of media
   */
  function revokeApproval(uint256 tokenId) external;

  /**
   * @notice Update the token URI
   */
  function updateTokenURI(uint256 tokenId, string calldata tokenURI) external;

  /**
   * @notice Update the token metadata uri
   */
  function updateTokenMetadataURI(uint256 tokenId, string calldata metadataURI) external;

  /**
   * @notice EIP-712 permit method. Sets an approved spender given a valid signature.
   */
  function permit(
    address spender,
    uint256 tokenId,
    EIP712Signature calldata sig
  ) external;

  // Added for App

  /**
   * @notice Set the ask on a piece of media
   */
  function setAskFromApp(uint256 tokenId, IMarket.Ask calldata ask) external;

  /**
   * @notice Set the bid on a piece of media
   */
  function setBidFromApp(uint256 tokenId, IMarket.Bid memory bid, address sender) external;

  /**
   * @notice Remove the bid on a piece of media
   */
  function removeBidFromApp(uint256 tokenId, address sender) external;

  function removeLazyBidFromApp(uint256 dropId, string memory name, address sender) external;

  function acceptBidFromApp(uint256 tokenId, IMarket.Bid memory bid, address sender) external;

  function acceptLazyBidFromApp(uint256 dropId, IDrop.TokenType memory tokenType, ILux.Token memory token, IMarket.Bid memory bid) external;

  function mintToken(address owner, ILux.Token memory tokenID) external returns (ILux.Token memory);

  function burnToken(address owner, uint256 tokenID) external;

  function tokenExists(uint256 tokenID) external returns (bool);

  function tokenCreator(uint256 tokenID) external returns (address);

  function previousTokenOwner(uint256 tokenID) external returns (address);

  function setLazyBidFromApp(
    uint256 dropId,
    IDrop.TokenType memory tokenType,
    IMarket.Bid memory bid,
    address spender
  ) external;

}

